import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  FlatList,
  ImageBackground,
} from "react-native";
import { Layout, Section, SectionContent } from "react-native-rapi-ui";
import { Button } from "react-native-rapi-ui";
import { FlatGrid } from 'react-native-super-grid';
import AddGroupBuy from "./AddGroupBuy";
import { createStackNavigator } from "@react-navigation/stack";
import ViewOrdersScreen from "./ViewOrdersScreen";
const Stack = createStackNavigator();
import { Ionicons } from "@expo/vector-icons";
import firebase from "../database/firebaseDB";

const db = firebase.firestore().collection("shopInfo");

function GroupBuyScreen({ navigation }) {
  const [shopInfo, setShopInfo] = useState([]);

  useEffect(() => {
    const unsubscribe = firebase
      .firestore()
      .collection("shopInfo")
      .onSnapshot((collection) => {
        const updatedRemarks = collection.docs.map((doc) => doc.data());
        setShopInfo(updatedRemarks);
      });

    return () => {
      unsubscribe();
    };
  }, []);

  // The function to render each row in our FlatList
  function renderItem({ item }) {
    return (
      <View style={styles.itemContainer}>
        <Text style={{textAlign: "center"}}>{item.shopLocation}</Text>
        <View
          style={styles.container}>
          <TouchableOpacity style={styles.icon} onPress={() => deleteNote(item.id)}>
            <Ionicons name="trash" size={30} color="#944" />
          </TouchableOpacity>
          <Layout>
            <Section>
              <SectionContent>
                {/* need to change to image */}
                  <Button
                    style={styles.button}
                    text="Order Group buy "
                    onPress={() =>
                      navigation.navigate("AddGroupBuy", {
                        newShopName: item.shopName,
                      })
                    }
                    size="lg"
                  />
                <View style={styles.button}>
                  <Button
                    text="View Last orders "
                    onPress={() => navigation.navigate("ViewOrdersScreen")}
                    size="lg"
                  />
                </View>
              </SectionContent>
            </Section>
          </Layout>
        </View>
      </View>
    );
  }

  return (
    <FlatGrid
        itemDimension={"2"}
        data={shopInfo}
        style={styles.gridView}
        // staticDimension={300}
        // fixed
        spacing={10}
        renderItem={renderItem}
        // keyExtractor={(item) => item.id.toString()}
      />
  );
}

export default function GroupBuyStack() {
  return (
    <Stack.Navigator>
      <Stack.Screen
        options={{ headerTitle: "Join a Group Buy" }}
        name="GroupBuyScreen"
        component={GroupBuyScreen}
      />
      <Stack.Screen
        options={{ headerTitle: "Add Food Order" }}
        name="AddGroupBuy"
        component={AddGroupBuy}
      />
      <Stack.Screen
        options={{ headerTitle: "View Current Orders" }}
        name="ViewOrdersScreen"
        component={ViewOrdersScreen}
      />
    </Stack.Navigator>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#ffc",
    alignItems: "center",
    justifyContent: "center",
  },
  listImage: {
    width: 100,
    height: 100,
  },
  button: {
    padding: 20,
    margin: 10,
  },
  btnText: {
    color: "white",
    fontSize: 20,
    justifyContent: "center",
    textAlign: "center",
  },
  gridView: {
    marginTop: 10,
    flex: 1,
  },
  itemContainer: {
    width: '100%',
    justifyContent: 'flex-end',
    borderRadius: 5,
    padding: 10,
    height: 150,
  },
  itemName: {
    fontSize: 16,
    color: '#fff',
    fontWeight: '600',
  },
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  itemCode: {
    fontWeight: '600',
    fontSize: 12,
    color: '#fff',
  },
  icon: {
    width: "100%",
    justifyContent: "flex-end",
  },
});
